const { XMLParser, XMLBuilder } = require("fast-xml-parser");

exports.parse = function WaraParse(text) {
    const parser = new XMLParser();
    return parser.parse(text);
}

exports.rebuild = function WaraBuild(data) {
    const builder = new XMLBuilder({
        format: true,
    });

    let result = builder.build(data);
    let step = '<?xml version="1.0" encoding="UTF-8"?>' + result;
    let final = step.replace('<?xml version="1.0" encoding="UTF-8"?><?xml></?xml>', '<?xml version="1.0" encoding="UTF-8"?>');

    return final;
}